#!/usr/bin/python3

replacements = []
with open('replacements') as f:
    for line in f:
        src, sep, target = line.strip().partition(' => ')
        replacements.append((src, target))

molecule = ""

transforms = {}
for src, target in replacements:
    segments = molecule.split(src)
    for i in range(1, len(segments)):
        newstr = src.join(segments[0:i]) + target + src.join(segments[i:])
        transforms[newstr] = True

print("TRANSFORMS: {0}".format(len(transforms)))
